    const [meals, setMeals] = useState([]); //state that will be used to change the state of loaded info

    useEffect(() => {
        const fetchMeals = async ()=>{
            const response = await fetch("https://react-http-test-713d8-default-rtdb.firebaseio.com/meals.json");
            const responseData = await response.json()

            const loadedMeals = []

            for( const key in responseData){
                loadedMeals.push({
                    id:responseData[key].id,
                    name: responseData[key].name,
                    description: responseData[key].description,
                    price: responseData[key].price,    })

            }
            setMeals(loadedMeals) //passed loaded meals from firebase

        }
fetchMeals()
