import {useEffect, useState} from "react";

import Card from '../UI/Card'
import MealItem from './MealItem'
import classes from './AvailableMeals.module.css'


const AvailableMeals = (props) => {

    const [meals, setMeals] = useState([]); //state that will be used to change the state of loaded info
    const [isLoading, setIsloading] = useState(true)
    const [httpError, setHttpError] = useState(false) //display http error message if not true
    useEffect(() => {
        const fetchMeals = async () => {
            const response = await fetch('https://react-http-test-713d8-default-rtdb.firebaseio.com/meals');

            if (!response.ok) {
                throw new Error("Something went wrong")
            }
            const responseData = await response.json();

            const loadedMeals = [];

            for (const key in responseData) {
                loadedMeals.push({
                    id: key,
                    name: responseData[key].name,
                    description: responseData[key].description,
                    price: responseData[key].price,
                });
            }

            setMeals(loadedMeals);
            setIsloading(false)
        };
//try to be able to await for the error to be displayed in the browser

            fetchMeals().catch((error) => {
                setIsloading(false)
                setHttpError(error.message)
            });



    }, []);

    if (isLoading) {
        return <section className={classes.mealsLoading}>
            <p>Loading...</p>
        </section>
    }

    if(httpError){
        return <section className={classes.mealsError}>
            <p>{httpError}</p>
        </section>
    }
//helper variable
    const mealsList = meals.map((meal) =>
        <MealItem
            key={meal.id}
            id={meal.id}
            name={meal.name}
            description={meal.description}
            price={meal.price}/>)
    return <section className={classes.meals}>

        <Card>
            <ul> {mealsList}</ul>

        </Card>


    </section>
}

export default AvailableMeals
